BEGIN
  SET NOCOUNT ON;
  DECLARE @CurrentDate DATETIME = GETUTCDATE();

  IF @UserCreated IS NULL
  BEGIN
    SET @ErrorCode = 1;
    SET @ErrorMessage = 'Invalid parameter';
    RETURN;
  END

  IF @ProfileName IS NULL
  BEGIN
    SET @ErrorCode = 2;
    SET @ErrorMessage = 'Invalid parameter';
    RETURN;
  END

  IF EXISTS (SELECT [ProfileId] FROM [Profile] WHERE [ProfileName] = @ProfileName)
  BEGIN
    SET @ErrorCode = 3;
    SET @ErrorMessage = 'Invalid parameter';
    RETURN;
  END

  IF EXISTS (SELECT [ModuleId] FROM @ModuleList WHERE [ModuleId] NOT IN (SELECT [ModuleId] FROM [Module]))
  BEGIN
    SET @ErrorCode = 4;
    SET @ErrorMessage = 'Invalid parameter';
    RETURN;
  END

  BEGIN TRANSACTION;
  BEGIN TRY
    SET @ProfileId = NEWID();

    INSERT INTO [Profile_Module] (
      [ProfileId],
      [ModuleId],
      [Created],
      [Updated],
      [UserCreated],
      [UserUpdated]
    )
    SELECT
      @ProfileId,
      [ModuleId],
      @CurrentDate,
      @CurrentDate,
      @UserCreated,
      @UserCreated
    FROM @ModuleList;

    INSERT INTO [Profile]
    (
      [ProfileId],
      [ProfileName],
