/*
================================================================================
  File name          : Save_Profile.sql
  Description        : save profile store procedure
  Autor              : Samir Diaz
  Created date       : 2-10-2024
  Last update        : 2-10-2024 by Samir Diaz
  Vertion            : 1.0
  Notes              : notes
================================================================================
*/
CREATE PROCEDURE [dbo].[Save_Profile]
  @ProfileName VARCHAR(50) = NULL,
  @ModuleList Type_ModuleList READONLY,
  @UserCreated UNIQUEIDENTIFIER = NULL,
  @ErrorCode INT OUT,
  @ErrorMessage VARCHAR(MAX) OUT,
  @ProfileId UNIQUEIDENTIFIER OUT
AS

BEGIN
  SET NOCOUNT ON;
  DECLARE @CurrentDate DATETIME = GETUTCDATE();

  IF @UserCreated IS NULL
  BEGIN
    SET @ErrorCode = 1;
    SET @ErrorMessage = 'Invalid parameter';
    RETURN;
  END

  IF @Email IS NULL
  BEGIN
    SET @ErrorCode = 2;
    SET @ErrorMessage = 'Invalid parameter';
    RETURN;
  END

  IF @UserName IS NULL
  BEGIN
    SET @ErrorCode = 3;
    SET @ErrorMessage = 'Invalid parameter';
    RETURN;
  END

  IF @Password IS NULL
  BEGIN
    SET @ErrorCode = 4;
    SET @ErrorMessage = 'Invalid parameter';
    RETURN;
  END

  IF EXISTS (SELECT [UserId] FROM [User] WHERE [Email] = @Email)
  BEGIN
    SET @ErrorCode = 5;
    SET @ErrorMessage = 'Invalid parameter';
    RETURN;
  END

  IF @ProfileId IS NOT NULL AND NOT EXISTS (SELECT [ProfileId] FROM [Profile] WHERE [ProfileId] = @ProfileId)
  BEGIN
    SET @ErrorCode = 6;
    SET @ErrorMessage = 'Invalid parameter';
    RETURN;
  END

  BEGIN TRANSACTION;
  BEGIN TRY
    SET @UserId = NEWID();

    INSERT INTO [User]
    (
      [UserId],
      [Email],
      [UserName],
      [Password],
      [ProfileId],
      [Created],
      [Updated],
      [UserCreated],
      [UserUpdated]
    )
    VALUES
   (
     @UserId,
     @Email,
     @UserName,
     @Password,
     @ProfileId,
     @CurrentDate,
     @CurrentDate,
     @UserCreated,
     @UserCreated
   );

   COMMIT TRANSACTION;
  END TRY
  BEGIN CATCH
    SET @ErrorCode = ERROR_NUMBER();
    SET @ErrorMessage = ERROR_MESSAGE();

    ROLLBACK TRANSACTION;
  END CATCH
END

/*
================================================================================
File name          : SaveUser.sql
Description        : Save use store procedure
Autor              : Samir Diaz
Created date       : 2-10-2024
Last update        : 2-10-2024 by Samir Diaz
Vertion            : 1.0
Notes              : notes
================================================================================
*/
CREATE PROCEDURE [dbo].[Save_User]
  @Email VARCHAR(200) = NULL,
  @UserName VARCHAR(150) = NULL,
  @Password VARCHAR(200) = NULL,
  @ProfileId UNIQUEIDENTIFIER = NULL,
  @UserCreated UNIQUEIDENTIFIER = NULL,
  @ErrorCode INT OUT,
  @ErrorMessage VARCHAR(MAX) OUT,
  @UserId UNIQUEIDENTIFIER OUT
AS

BEGIN SET NOCOUNT ON;
SELECT A.[ModuleId], B.[ModuleName], B.[ModulePath], B.[Created], B.[Updated], B.[UserCreated], B.[UserUpdated] FROM [Profile_Module] A INNER JOIN [Module] B ON A.[ModuleId] = B.[ModuleId] AND B.[IsActive] = 1
END

BEGIN SET NOCOUNT ON;
DECLARE @Offset INT = (@Page - 1) * @RowsPerPage; SELECT @Rows = COUNT(*) FROM [module];
SELECT [moduleId], [Created], [Updated], [UserCreated], [UserUpdated], [IsActive] FROM [module] ORDER BY [moduleId] OFFSET @Offset ROWS FETCH NEXT @RowsPerPage ROWS ONLY; END

BEGIN SET NOCOUNT ON;
DECLARE @Offset INT = (@Page - 1) * @RowsPerPage; SELECT @Rows = COUNT(*) FROM [Profile];
SELECT [ProfileId], [ProfileName], [Created], [Updated], [UserCreated], [UserUpdated], [IsActive] FROM [Profile] ORDER BY [ProfileId] OFFSET @Offset ROWS FETCH NEXT @RowsPerPage ROWS ONLY; END

CREATE PROCEDURE [dbo].[Select_User]
  @Email VARCHAR(200) = NULL,
  @UserId UNIQUEIDENTIFIER = NULL
AS

BEGIN SET NOCOUNT ON;
DECLARE @Offset INT = (@Page - 1) * @RowsPerPage; SELECT @Rows = COUNT(*) FROM [User];
SELECT [UserId], [Email], [UserName], [Created], [Updated], [UserCreated], [UserUpdated], [IsActive] FROM [User] ORDER BY [UserId] OFFSET @Offset ROWS FETCH NEXT @RowsPerPage ROWS ONLY END

CREATE PROCEDURE [dbo].[Select_UserList]
  @Page INT = 1,
  @RowsPerPage INT = 15,
  @Rows INT OUT
AS

BEGIN
  SET NOCOUNT ON;
  DECLARE @CurrentDate DATETIME = GETUTCDATE();

  IF @ProfileId IS NULL
  BEGIN
    SET @ErrorCode = 1;
    SET @ErrorMessage = 'Ivalid parameter';
    RETURN;
  END

  IF @UserUpdated IS NULL
  BEGIN
    SET @ErrorCode = 2;
    SET @ErrorMessage = 'Ivalid parameter';
    RETURN;
  END

  IF EXISTS (SELECT [ProfileId] FROM [Profile] WHERE [ProfileName] = @ProfileName AND [ProfileId] != @ProfileId)
	BEGIN
		SET @ErrorCode = 3;
		SET @ErrorMessage = 'Invalid parameter';
		RETURN;
	END

	IF EXISTS (SELECT [ModuleId] FROM @ModuleList WHERE [ModuleId] NOT IN (SELECT [ModuleId] FROM [Module]))
  BEGIN
    SET @ErrorCode = 4;
    SET @ErrorMessage = 'Invalid parameter';
    RETURN;
  END

  BEGIN TRANSACTION;
  BEGIN TRY
		UPDATE [Profile_Module]
		SET [IsActive] = 0
		WHERE [ProfileId] = @ProfileId
		AND [ModuleId] NOT IN (SELECT A.[ModuleId] FROM @ModuleList A)

		INSERT INTO [Profile_Module] (
      [ProfileId],
      [ModuleId],
      [Created],
      [Updated],
      [UserCreated],
      [UserUpdated]
    )
		SELECT
			@ProfileId,
      A.[ModuleId],
      @CurrentDate,
      @CurrentDate,
      @UserUpdated,
      @UserUpdated
		FROM @ModuleList A
		LEFT JOIN [Profile_Module] B ON A.[ModuleId] = B.[ModuleId] AND B.[ProfileId] = @ProfileId
		WHERE B.[ModuleId] IS NULL;

    UPDATE [Profile]
    SET [ProfileName] = @ProfileName,
				[IsActive] = @IsActive,
        [Updated] = @CurrentDate,
        [UserUpdated] = @UserUpdated
    WHERE [ProfileId] = @ProfileId

    COMMIT TRANSACTION;
  END TRY
  BEGIN CATCH
    SET @ErrorCode = ERROR_NUMBER();
    SET @ErrorMessage = ERROR_MESSAGE();

    ROLLBACK TRANSACTION;
  END CATCH
END

/*
================================================================================
  File name          : Update_Profile.sql
  Description        : update profile store procedure
  Autor              : Samir Diaz
  Created date       : 2-10-2024
  Last update        : 2-10-2024 by Samir Diaz
  Vertion            : 1.0
  Notes              : notes
================================================================================
*/
CREATE PROCEDURE [Update_Profile]
  @ProfileId UNIQUEIDENTIFIER = NULL,
  @ProfileName VARCHAR(150) = NULL,
  @ModuleList Type_ModuleList READONLY,
  @IsActive BIT = 1,
  @UserUpdated UNIQUEIDENTIFIER = NULL,
  @ErrorCode INT OUT,
  @ErrorMessage VARCHAR(MAX) OUT
AS

BEGIN
  SET NOCOUNT ON;
  DECLARE @CurrentDate DATETIME = GETUTCDATE();

	IF @UserId IS NULL
	BEGIN
		SET @ErrorCode = 1;
		SET @ErrorMessage = 'Invalid parameter';
		RETURN;
	END

  IF @UserUpdated IS NULL
  BEGIN
    SET @ErrorCode = 1;
    SET @ErrorMessage = 'Ivalid parameter';
    RETURN;
  END

  IF @Email IS NULL
	BEGIN
		SET @ErrorCode = 2;
		SET @ErrorMessage = 'Invalid parameter';
		RETURN;
	END

	IF @UserName IS NULL
	BEGIN
		SET @ErrorCode = 3;
		SET @ErrorMessage = 'Invalid parameter';
		RETURN;
	END

	IF @Password IS NULL
	BEGIN
		SET @ErrorCode = 4;
		SET @ErrorMessage = 'Invalid parameter';
		RETURN;
	END

	IF EXISTS (SELECT [UserId] FROM [User] WHERE [Email] = @Email AND [UserId] != @UserId)
	BEGIN
		SET @ErrorCode = 5;
		SET @ErrorMessage = 'Invalid parameter';
		RETURN;
	END

  BEGIN TRANSACTION;
  BEGIN TRY
    UPDATE [User]
    SET [Email] = @Email,
        [UserName] = @UserName,
				[Password] = @Password,
				[ProfileId] = @ProfileId,
				[IsActive] = @IsActive,
        [Updated] = @CurrentDate,
        [UserUpdated] = @UserUpdated
    WHERE [UserId] = @UserId

    COMMIT TRANSACTION;
  END TRY
  BEGIN CATCH
    SET @ErrorCode = ERROR_NUMBER();
    SET @ErrorMessage = ERROR_MESSAGE();

    ROLLBACK TRANSACTION;
  END CATCH
END

